mapper.select:
select
id, dict_type, dict_name, dict_code, description, status, sort
from dict


        and id = #{id}


        and dict_type = #{dictType}


        and dict_name = #{dictName}


        and dict_code = #{dictCode}


        and description = #{description}


        and status = #{status}


        and sort = #{sort}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from dict


        and id = #{id}


        and dict_type = #{dictType}


        and dict_name = #{dictName}


        and dict_code = #{dictCode}


        and description = #{description}


        and status = #{status}


        and sort = #{sort}

