mapper.select:
select
  id, question_id, exam_paper_user_id, answer, `right`, score, points, previous, `next`, create_time, create_by, update_time, update_by
from exam_paper_detail

    1 = 1

        and question_id = #{questionId}


        and exam_paper_user_id = #{examPaperUserId}


        and answer = #{answer}


        and `right` = #{right}


        and score = #{score}


        and points = #{points}


        and previous = #{previous}


        and `next` = #{next}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}

select count(1)
from exam_paper_detail


        and id = #{id}


        and question_id = #{questionId}


        and exam_paper_user_id = #{examPaperUserId}


        and answer = #{answer}


        and `right` = #{right}


        and score = #{score}


        and points = #{points}


        and previous = #{previous}


        and `next` = #{next}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
