mapper.select:
select
id, user_id, user_name, exam_paper_id, score, batch, status, create_time, create_by, update_time, update_by
from exam_paper_user

    1 = 1

        and user_name = #{userName}


        and exam_paper_id = #{examPaperId}


        and score = #{score}


        and batch = #{batch}


        and status = #{status}

select count(1)
from exam_paper_user


        and id = #{id}


        and user_id = #{userId}


        and user_name = #{userName}


        and exam_paper_id = #{examPaperId}


        and score = #{score}


        and batch = #{batch}


        and status = #{status}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}
