mapper.select:
select
  id, question, answer, `explain`, image_url, `type`, exam_type, source_id
from exam_question_info


        and question = #{question}


        and answer = #{answer}


        and `explain` = #{explain}


        and image_url = #{imageUrl}


        and `type` = #{type}


        and exam_type = #{examType}


        and source_id = #{sourceId}

select count(1)
from exam_question_info


        and id = #{id}


        and question = #{question}


        and answer = #{answer}


        and explain = #{explain}


        and image_url = #{imageUrl}


        and type = #{type}


        and exam_type = #{examType}


        and source_id = #{sourceId}
